import React, { Component } from 'react';
import '../src/assets/css/login.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import md5 from 'md5';
import Cookies from 'universal-cookie';

var baseUrl="http://localhost:8080/getUser=";
const cookies = new Cookies();

class Login extends Component {
    state={
        form:{
            username: '',
            password: ''
        }
    }

    handleChange=async e=>{
        await this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value
            }
        });
    }

    iniciarSesion=async()=>{

        console.log(baseUrl+
                    this.state.form.username+
                    '&pass='+
                    md5(this.state.form.password))

        //if(this.state.form.username === "jorge" &&
        //this.state.form.password === "1234"){
        //    console.log("Welcome!!!")
        //    window.location.href="http://localhost:3000/admin/dashboard";
        //}else{
        //    console.log("DENEGADO!!!")
        //    
        //}

        await axios.get(baseUrl + this.state.form.username+
                           '&pass=' + md5(this.state.form.password))
            .then(response=>{
                console.log(response)
                if(response > 0){
                    window.location.href="http://localhost:3000/admin/dashboard";
                }else{
                    alert('Usuario o Contraseña Incorrectos');
                    window.location.href="/";
                }
        })
    }

    componentDidMount() {
        if(cookies.get('username')){
            window.location.href="./menu";
        }
    }


    render() {
        return (
            <div className="contPrincipal">
                <div className="contSecundario">
                <div className="form-group">
                    <label>Usuario: </label>
                    <br />
                    <input
                    type="text"
                    className="form-control"
                    name="username"
                    onChange={this.handleChange}
                    />
                    <br />
                    <label>Contraseña: </label>
                    <br />
                    <input
                    type="password"
                    className="form-control"
                    name="password"
                    onChange={this.handleChange}
                    />
                    <br />
                    <button className="btn btn-primary" onClick={()=> this.iniciarSesion()}>Iniciar Sesión</button>
                </div>
                </div>
            </div>
        );
    }
}

export default Login;
