import { Line} from "react-chartjs-2";
// reactstrap components
import {Card, CardHeader, CardBody, CardFooter, 
  UncontrolledAlert, CardTitle, Row, Col,} from "reactstrap";

//import {dashboardTEMPChart} from "variables/charts.js"
import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button } from "bootstrap";
import { resolveModuleName } from "typescript";

var parametro = "Temperatura";


const cambioParametro = (param) =>{
  if (param == 'PH')
    parametro = 'PH';
  if (param == 'oxigeno')
    parametro = 'Oxígeno';
  if (param == 'salinidad')
    parametro = 'Salinidad';
  if (param == 'profundidad')
    parametro = 'Profundidad';
  if (param == 'conductividad')
    parametro = 'Conductividad';
  if (param == 'oxireduc')
    parametro = 'Oxígeno Reducido';
  if (param == 'temperatura')
    parametro = 'Temperatura';
}


function Dashboard() {

  const [arrayTemp, setArrayTemp] = useState();
  const [lastTemp, setLastTemp] = useState(); 

  const Horas = ["00:00", "01:00", "02:00", "03:00", "04:00", "05:00", "06:00", "07:00", "08:00", "09:00", "10:00", "11:00", "12:00", "13:00","14:00","15:00","16:00","17:00","18:00","19:00","20:00","21:00","22:00","23:00","24:00"]
  const url = "http://localhost:8080/getTempByPool=1&year=2021&month=11&day=1"
  let cardTemp = "";

  const funcionPrint = () => {
    window.print()
  };

  const openSidebar = () => {
    document.documentElement.classList.toggle("nav-open");
    sidebarToggle.current.classList.toggle("toggled");
  };


  const cambioParametro = (param) =>{
    if (param == 'PH')
      parametro = 'PH';
    if (param == 'oxigeno')
      parametro = 'Oxígeno';
    if (param == 'salinidad')
      parametro = 'Salinidad';
    if (param == 'profundidad')
      parametro = 'Profundidad';
    if (param == 'conductividad')
      parametro = 'Conductividad';
    if (param == 'oxireduc')
      parametro = 'Oxígeno Reducido';
    if (param == 'temperatura')
      parametro = 'Temperatura';
    let tempHoy=[];
    axios.get(url)
    .then(response=> {
      for (let index = 0; index < response.data.length; index++) {
        tempHoy[index] = response.data[index].temp
      }
      setArrayTemp(tempHoy);
      setLastTemp(response.data[response.data.length-1].temp);
    })
  }



  const sidebarToggle = React.useRef();

  useEffect(async () => {
      let tempHoy=[];
      await axios.get(url)
      .then(response=> {
        for (let index = 0; index < response.data.length; index++) {
          tempHoy[index] = response.data[index].temp
        }
        setArrayTemp(tempHoy);
        setLastTemp(response.data[response.data.length-1].temp);
      })
  },[]);

  useEffect(() => {
    setInterval(() => {
      let tempHoy=[];
      axios.get(url)
      .then(response=> {
        for (let index = 0; index < response.data.length; index++) {
          tempHoy[index] = response.data[index].temp
        }
        setArrayTemp(tempHoy);
        setLastTemp(response.data[response.data.length-1].temp);
      })
    }, 1000*10);
  }, []);


  if (lastTemp > 20) {
    cardTemp= "fas fa-thermometer-three-quarters text-danger"
  }else{
    cardTemp= "fas fa-thermometer-three-quarters text-primary"
  }

  var dashboardTEMPChart = {
    data: (canvas) => {
      return {
        labels: Horas,
        datasets: [
          {
            data: arrayTemp,
            fill: false,
            borderColor: "#51CACF",
            backgroundColor: "transparent",
            pointBorderColor: "#51CACF",
            pointRadius: 4,
            pointHoverRadius: 4,
            pointBorderWidth: 8,
            tension: 0.4,
          },
        ],
      };
    },
    options: {
      plugins: {
        legend: { display: false },
      },
    },
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col lg="3" md="6" sm="6">

            <Card className="card-stats">
              <button
                type="button"
                //ref= {sidebarToggle}
                className="navbar-toggler"
                //onClick={() => openSidebar()}

                onClick= {() => cambioParametro('PH')}

              >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-danger"></i>

                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">PH</p>
                      <CardTitle tag="p"> 4.5 </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody> 
              </button>
            </Card>
          </Col>





          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('oxigeno')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-success"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Oxígeno</p>
                      <CardTitle tag="p"> 0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>
          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('salinidad')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                      <i className="fas fa-tint text-warning"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Salinidad</p>
                      <CardTitle tag="p">0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">

            <Card className="card-stats">
              <button
                type="button"
                //ref= {sidebarToggle}
                className="navbar-toggler"
                //onClick={() => openSidebar()}
                onClick= {() => cambioParametro('profundidad')}
              >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-danger"></i>

                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Profundidad</p>
                      <CardTitle tag="p"> 4.5 </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody> 
              </button>
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('conductividad')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-success"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Conductividad</p>
                      <CardTitle tag="p"> 0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('oxireduc')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                      <i className="fas fa-tint text-warning"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Oxígeno Reducido</p>
                      <CardTitle tag="p">0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('temperatura')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className= {cardTemp}></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Temperatura</p>
                      <CardTitle tag="p"> {lastTemp} </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
                <Row>

                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md="12">
            <Card className="card-chart">
              <CardHeader>
                <CardTitle tag="h5">{parametro} en Pileta</CardTitle>
                <p className="card-category">Ultimas 24 Horas</p>
              </CardHeader>
              <CardBody>
                <Line 
                  data={dashboardTEMPChart.data}
                  options={dashboardTEMPChart.options}
                  width={400}
                  height={140}
                />
              </CardBody>
            </Card>
          </Col>
        </Row>

      <Row>
      <Col md="2"></Col>
        <Col ><CardTitle tag="h5">Tabla de Datos:</CardTitle></Col>
        <Col md="2"></Col>
      </Row>
      <Row>
      <Col md="2"></Col>
      <Col><p className="card-category">Ultimas 24 Horas</p></Col>
        <Col md="2"></Col>
      </Row>


        <Row>
          <Col md="2"></Col>
          <Col>

        <table class="table">
          <thead>
            <tr>
              <th scope="col">Fecha</th>
              <th scope="col">Hora</th>
              <th scope="col">{parametro}</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>22/2/2022</td>
              <td>10:00</td>
              <td>17°</td>
            </tr>
            <tr>
              <td>22/2/2022</td>
              <td>11:00</td>
              <td>17.2°</td>
            </tr>
            <tr>
              <td>22/2/2022</td>
              <td>12:00</td>
              <td>18°</td>
            </tr>
          </tbody>
        </table>
        </Col>
        <Col md="2"></Col>
        </Row>
        <Row>
        <Col md="8"></Col>
        <input type="button" value="Descargar" class="btn btn-danger btn-sm" onClick={() => funcionPrint()} />
        </Row>


      </div>

    </>


  );




}



export default Dashboard;
