import { Line} from "react-chartjs-2";
// reactstrap components
import {Card, CardHeader, CardBody, CardFooter, 
  UncontrolledAlert, CardTitle, Row, Col,} from "reactstrap";

//import {dashboardTEMPChart} from "variables/charts.js"
import React, { useState, useEffect } from "react";
import axios from 'axios';


var parametro = "Temperatura";
//var url = "http://10.42.0.1:8080/getTempByPool=1&year=2022&month=3&day=3"
//var urlPh = "http://10.42.0.1:8080/getPhByPool=1&year=2022&month=3&day=3"
var url = "http://localhost:8080/getTempByPool=1&year=2021&month=11&day=1"
var urlPh = "http://localhost:8080/getPhPool=1&year=2022&month=3&day=3"

let arrayParametro=[];





function Dashboard() {


  const [arrayTemp, setArrayTemp] = useState();
  const [lastTemp, setLastTemp] = useState(); 

  const [arrayPh, setArrayPh] = useState();
  const [lastPh, setLastPh] = useState(); 

  const Horas = ["00:00", "01:00", "02:00", "03:00", "04:00", "05:00", "06:00", "07:00", "08:00", "09:00", "10:00", "11:00", "12:00", "13:00","14:00","15:00","16:00","17:00","18:00","19:00","20:00","21:00","22:00","23:00","24:00"]

  let cardTemp = "";

  const funcionPrint = () => {
    //window.print()
    var mywindow = window.open('', 'PRINT', 'height=400,width=600');
    mywindow.document.write('<html><h2>Tabla de Datos:</h2> Estación de Piscicultura Río Grande - últimas 24 horas<head>');
	  mywindow.document.write('<style>.table{width:100%;border-collapse:collapse;margin:16px 0 16px 0;}.table th{border:1px solid #ddd;padding:4px;background-color:#d4eefd;text-align:center;font-size:15px;}.table td{border:1px solid #ddd;text-align:center;padding:6px;}</style>');
    mywindow.document.write('</head><body >');
    mywindow.document.write(document.getElementById('muestra').innerHTML);
    mywindow.document.write('</body></html>');
    mywindow.document.close();
    mywindow.focus();
    mywindow.print();
    mywindow.close();
    return true;
  };

  const openSidebar = () => {
    document.documentElement.classList.toggle("nav-open");
    sidebarToggle.current.classList.toggle("toggled");
  };

  function recargar(){
    let tempHoy=[];
    let phHoy=[];
    axios.all([
      axios.get(url),
      axios.get(urlPh)
    ]).then(axios.spread((response1, response2) => {
      if(response1.data.length > 24){
        for (let index = response1.data.length-24, j=0; index < response1.data.length; index++, j++) {
          tempHoy[j] = response1.data[index].temp
          phHoy[j] = response2.data[index].ph
        }
      }else{
        for (let index = 0; index < response1.data.length; index++) {
          tempHoy[index] = response1.data[index].temp
          phHoy[index] = response2.data[index].ph
        }
      }

      setArrayTemp(tempHoy);
      setLastTemp(response1.data[response1.data.length-1].temp);

      setArrayPh(phHoy);
      setLastPh(response2.data[response2.data.length-1].ph);
      console.log(lastPh);
    })


    /*
    axios.get(url)
    .then(response=> {
      if(response.data.length > 24){
        for (let index = response.data.length-24, j=0; index < response.data.length; index++, j++) {
          tempHoy[j] = response.data[index].temp
          phHoy[j] = response.data[index].ph
        }
      }else{
        for (let index = 0; index < response.data.length; index++) {
          tempHoy[index] = response.data[index].temp
          phHoy[index] = response.data[index].ph
        }
      }

      setArrayTemp(tempHoy);
      setLastTemp(response.data[response.data.length-1].temp);

      setArrayPh(phHoy);
      setLastPh(response.data[response.data.length-1].ph);
      console.log(lastPh);
    })
    */
    /*axios.get(urlPh)
    .then(response=> {
      if(response.data.length > 24){
        for (let index = response.data.length-24, j=0; index < response.data.length; index++, j++) {
          phHoy[j] = response.data[index].ph
        }
      }else{
        for (let index = 0; index < response.data.length; index++) {
          phHoy[index] = response.data[index].ph
        }
      }

      setArrayPh(phHoy);
      setLastPh(response.data[response.data.length-1].ph);
    })
    */

    ///////////////////////////////////////////////////////////////
    /*.then(response=> {
      for (let index = 0; index < response.data.length; index++) {
        tempHoy[index] = response.data[index].temp
      }
      setArrayTemp(tempHoy);
      setLastTemp(response.data[response.data.length-1].temp);
    })*/
    )};

  const cambioParametro = (param) =>{
    if (param == 'PH'){
      parametro = 'PH';
      //arrayParametro = arrayPh;
    }
    if (param == 'oxigeno'){
      parametro = 'Oxígeno';
    }
    if (param == 'salinidad'){
      parametro = 'Salinidad';
    }
    if (param == 'profundidad'){
      parametro = 'Profundidad';
    } 
    if (param == 'conductividad'){
      parametro = 'Conductividad';
    }
    if (param == 'oxireduc'){
      parametro = 'Oxígeno Reducido';
    }
    if (param == 'temperatura'){
      parametro = 'Temperatura';
      //arrayParametro = arrayTemp;
    }
    recargar();
  }


  const sidebarToggle = React.useRef();

  useEffect(async () => {
      let phHoy=[];
      let tempHoy=[];
      await axios.get(url)
      .then(response=> {
        if(response.data.length > 24){
          for (let index = response.data.length-24, j=0; index < response.data.length; index++, j++) {
            tempHoy[j] = response.data[index].temp
            phHoy[j] = response.data[index].ph
          }
        }else{
          for (let index = 0; index < response.data.length; index++) {
            tempHoy[index] = response.data[index].temp
            phHoy[index] = response.data[index].ph
          }
        }

        setArrayTemp(tempHoy);
        setLastTemp(response.data[response.data.length-1].temp);

        setArrayPh(phHoy);
        setLastPh(response.data[response.data.length-1].ph);
        console.log(lastPh);
      })
  },[]);


  useEffect(() => {
    setInterval(() => {
      recargar()
    }, 1000*11);
  }, []);


  if (lastTemp > 20) {
    cardTemp= "fas fa-thermometer-three-quarters text-danger"
  }else{
    cardTemp= "fas fa-thermometer-three-quarters text-primary"
  }

  var dashboardTEMPChart = {
    data: (canvas) => {
      return {
        labels: Horas,
        datasets: [
          {
            data: arrayTemp,
            fill: false,
            borderColor: "#51CACF",
            backgroundColor: "transparent",
            pointBorderColor: "#51CACF",
            pointRadius: 4,
            pointHoverRadius: 4,
            pointBorderWidth: 8,
            tension: 0.4,
          },
        ],
      };
    },
    options: {
      plugins: {
        legend: { display: false },
      },
    },
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col lg="3" md="6" sm="6">

            <Card className="card-stats">
              <button
                type="button"
                //ref= {sidebarToggle}
                className="navbar-toggler"
                //onClick={() => openSidebar()}

                onClick= {() => cambioParametro('PH')}

              >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-danger"></i>

                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">PH</p>
                      <CardTitle tag="p"> {lastPh} </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody> 
              </button>
            </Card>
          </Col>





          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('oxigeno')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-success"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Oxígeno</p>
                      <CardTitle tag="p"> {lastTemp} </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>
          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
            <button
              type="button"
              //ref= {sidebarToggle}
              className="navbar-toggler"
              //onClick={() => openSidebar()}
              onClick= {() => cambioParametro('salinidad')}
            >
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                      <i className="fas fa-tint text-warning"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Salinidad</p>
                      <CardTitle tag="p">{lastTemp} </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
              </button>
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">

            <Card className="card-stats">
