import { Line} from "react-chartjs-2";
// reactstrap components
import {Card, CardHeader, CardBody, CardFooter, 
               CardTitle, Row, Col,} from "reactstrap";

//import {dashboardTEMPChart} from "variables/charts.js"
import React, { useState, useEffect } from "react";
import axios from 'axios';


function Dashboard() {  
    const [arrayTemp, setArrayTemp] = useState();
    const [lastTemp, setLastTemp] = useState();

    const url = "http://localhost:8080/getTempByPool=1&year=2021&month=11&day=1"
    let cardTemp = [];

    useEffect(async () => {
        let tempHoy=[];
        await axios.get(url)
        .then(response=> {
          for (let index = 0; index < response.data.length; index++) {
            tempHoy[index] = response.data[index].temp
          }
          setArrayTemp(tempHoy);
          setLastTemp(response.data[response.data.length-1].temp);
        })
    },[]);

    if (lastTemp > 12.00) {
      cardTemp= "fas fa-thermometer-three-quarters text-danger"
    }else{
      cardTemp= "fas fa-thermometer-three-quarters text-primary"
    }

    console.log(arrayTemp);
    const Horas = ["00:00", "01:00", "02:00", "03:00", "04:00", "05:00", "06:00", "07:00", "08:00", "09:00", "10:00", "11:00", "12:00", "13:00","14:00","15:00","16:00","17:00","18:00","19:00","20:00","21:00","22:00","23:00","24:00"]
    var dashboardTEMPChart = {
    data: (canvas) => {
      return {
        labels: Horas,
        datasets: [
          {
            data: arrayTemp,
            fill: false,
            borderColor: "#51CACF",
            backgroundColor: "transparent",
            pointBorderColor: "#51CACF",
            pointRadius: 4,
            pointHoverRadius: 4,
            pointBorderWidth: 8,
            tension: 0.4,
          },
        ],
      };
    },
    options: {
      plugins: {
        legend: { display: false },
      },
    },
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-danger"></i>

                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">PH</p>
                      <CardTitle tag="p"> 4.5 </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody> 
            </Card>
          </Col>

          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className="fas fa-tint text-success"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Oxigeno</p>
                      <CardTitle tag="p"> 0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>

            </Card>
          </Col>
          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                      <i className="fas fa-tint text-warning"></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Salinidad</p>
                      <CardTitle tag="p">0.0</CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
            </Card>
          </Col>
          <Col lg="3" md="6" sm="6">
            <Card className="card-stats">
              <CardBody>
                <Row>
                  <Col md="4" xs="5">
                    <div className="icon-big text-center icon-warning">
                    <i className= {cardTemp}></i>
                    </div>
                  </Col>
                  <Col md="8" xs="7">
                    <div className="numbers">
                      <p className="card-category">Temperatura</p>
                      <CardTitle tag="p"> {lastTemp} </CardTitle>
                      <p />
                    </div>
                  </Col>
                </Row>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md="12">
            <Card className="card-chart">
              <CardHeader>
                <CardTitle tag="h5">Temperatura en Pileta</CardTitle>
                <p className="card-category">Ultimas 24 Horas</p>
              </CardHeader>
              <CardBody>
                <Line 
                  data={dashboardTEMPChart.data}
                  options={dashboardTEMPChart.options}
                  width={400}
                  height={100}
                />
              </CardBody>

            </Card>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default Dashboard;
