import { Line} from "react-chartjs-2";
// reactstrap components
import {Card, CardHeader, CardBody, CardFooter, 
  UncontrolledAlert, CardTitle, Row, Col,} from "reactstrap";

//import {dashboardTEMPChart} from "variables/charts.js"
import React, { useState, useEffect } from "react";
import axios from 'axios';
import { Button } from "bootstrap";

function Dashboard() {

  const [arrayTemp, setArrayTemp] = useState();
  const [lastTemp, setLastTemp] = useState(); 

  const Horas = ["00:00", "01:00", "02:00", "03:00", "04:00", "05:00", "06:00", "07:00", "08:00", "09:00", "10:00", "11:00", "12:00", "13:00","14:00","15:00","16:00","17:00","18:00","19:00","20:00","21:00","22:00","23:00","24:00"]
  const url = "http://localhost:8080/getTempByPool=1&year=2021&month=11&day=1"
  let cardTemp = "";

  const openSidebar = () => {
    document.documentElement.classList.toggle("nav-open");
    sidebarToggle.current.classList.toggle("toggled");
  };

  const sidebarToggle = React.useRef();

  useEffect(async () => {
      let tempHoy=[];
      await axios.get(url)
      .then(response=> {
        for (let index = 0; index < response.data.length; index++) {
          tempHoy[index] = response.data[index].temp
        }
        setArrayTemp(tempHoy);
        setLastTemp(response.data[response.data.length-1].temp);
      })
  },[]);

  useEffect(() => {
    setInterval(() => {
      let tempHoy=[];
      axios.get(url)
      .then(response=> {
        for (let index = 0; index < response.data.length; index++) {
          tempHoy[index] = response.data[index].temp
        }
        setArrayTemp(tempHoy);
        setLastTemp(response.data[response.data.length-1].temp);
      })
    }, 1000*60);
  }, []);


  if (lastTemp > 20) {
    cardTemp= "fas fa-thermometer-three-quarters text-danger"
  }else{
    cardTemp= "fas fa-thermometer-three-quarters text-primary"
  }

  var dashboardTEMPChart = {
    data: (canvas) => {
      return {
        labels: Horas,
        datasets: [
          {
            data: arrayTemp,
            fill: false,
            borderColor: "#51CACF",
            backgroundColor: "transparent",
            pointBorderColor: "#51CACF",
            pointRadius: 4,
            pointHoverRadius: 4,
            pointBorderWidth: 8,
            tension: 0.4,
          },
        ],
      };
    },
    options: {
      plugins: {
        legend: { display: false },
      },
    },
  };

  return (
    <>
      <div className="content">
        <Row>
          <Col lg="3" md="6" sm="6">

            <Card className="card-stats">
            <button
